ALTER TABLE oats_alr_appl_components ADD CONSTRAINT oats_apcp_chk3 CHECK (
	--
	-- Ensures only NON FARM USE has an optional
	-- relationship from NONFARM USE SUBTYPE CODE
	(
		(alr_change_code = 'NFU')
		AND (
			nonfarm_use_type_code IS NOT NULL
			AND nonfarm_use_subtype_code IS NOT NULL
		)
		OR (
			nonfarm_use_type_code IS NULL
			AND nonfarm_use_subtype_code IS NULL
		)
	)
	OR (
