ALTER TABLE oats_organizations ADD CONSTRAINT oats_org_chk CHECK (
	(
		org_type = 'BGO'
		AND ministry_cd IS NOT NULL
		AND organization_unit_cd IS NOT NULL
		AND alias_name IS NULL
	)
	OR (
		org_type = 'NBC'
		AND ministry_cd IS NULL
		AND organization_unit_cd IS NULL
	)
);

