CREATE TABLE
	oats_property_interests (
		property_interest_id bigint NOT NULL,
		prin_type varchar(10) NOT NULL,
		subject_property_id bigint NOT NULL,
		person_organization_id bigint,
		property_owner_type_code varchar(10),
		active_ind varchar(1),
		land_title_charge_code varchar(10),
		charge_nbr varchar(11),
		charge_desc varchar(4000),
		property_interest_code varchar(10),
		nonfarm_use_type_code varchar(10),
		who_created varchar(32) NOT NULL,
		when_created timestamp NOT NULL,
		who_updated varchar(32),
		when_updated timestamp,
		revision_count bigint NOT NULL DEFAULT 0
	);

COMMENT ON TABLE oats_property_interests IS 'A PROPERTY OWNER is the assertion that a PERSON ORGANIZATION is in possession of a PROPERTY at the time an ALR APPLICATION was submitted to the Commission or when an ISSUE was logged.  For the purposes of the ALC, the owner of Crown Land will be the ministry that has governance over the land.  For example: - Integrated Land Management Bureau (ILMB) - Ministry of Forest and Range (MoFR)  A LAND TITLE CHARGE is an interest in land (as identified by a LAND TITLE CHARGE CODE) that is registerable against the title to land of a SUBJECT PROPERTY that may impact one or more of the title holders (for example, a mortgage) or the usage of the land (for example, statutory right of way, mineral rights).  Only land title charges that are germane to the ALR APPLICATION will be included.A PROPERTY INTEREST is the generalization of PROPERTY OWNERSHIP and LAND TITLE CHARGE applied against a SUBJECT PROPERTY that is germane to the ALR APPLICATION.';

COMMENT ON COLUMN oats_property_interests.revision_count IS 'REVISION COUNT is used by Hibernate to control optimistic locking';

COMMENT ON COLUMN oats_property_interests.charge_desc IS 'Further description of the charge on the land';

COMMENT ON COLUMN oats_property_interests.who_created IS 'WHO CREATED is the IDIR or Oracle userid of the person who created the record.';

COMMENT ON COLUMN oats_property_interests.land_title_charge_code IS 'User supplied value to uniquely identify a LAND TITLE CHARGE CODE.  This is identical to the ALTOS VTRCDE.TRNSCTN_TYP_CD column.';

COMMENT ON COLUMN oats_property_interests.property_interest_code IS 'Indicates the nature of the person/orgs interest in the property';

COMMENT ON COLUMN oats_property_interests.active_ind IS 'Indicates whether or not the PROPERTY OWNERSHIP is active or not; no indicates retention of historical information.';

COMMENT ON COLUMN oats_property_interests.who_updated IS 'WHO UPDATED is the IDIR or Oracle userid of the person who last updated the record.';

COMMENT ON COLUMN oats_property_interests.subject_property_id IS 'System generated sequence number to uniquely identify a SUBJECT PROPERTY';

COMMENT ON COLUMN oats_property_interests.property_interest_id IS 'System generated sequence number to uniquely identify a PROPERTY INTEREST';

COMMENT ON COLUMN oats_property_interests.when_updated IS 'WHEN UPDATED is the date/time that the record was last updated.';

COMMENT ON COLUMN oats_property_interests.property_owner_type_code IS 'User supplied value to uniquely identify an PROPERTY OWNER TYPE CODE';

COMMENT ON COLUMN oats_property_interests.nonfarm_use_type_code IS 'Indicates the type of non-farm use employed by the interest.';

COMMENT ON COLUMN oats_property_interests.person_organization_id IS 'PERSON ORGANIZATION ID is the system generated unique identifier for PERSON ORGANIZATION.';

COMMENT ON COLUMN oats_property_interests.charge_nbr IS 'The CHARGE NBR is the number assigned by the Land Titles Office to identify the LAND TITLE CHARGE.  This is potentially sourced from the ALTOS system''s CHARG . CHARGE_NMBR table . colum.';

COMMENT ON COLUMN oats_property_interests.when_created IS 'WHEN CREATED is the date/time that the record was created.';

ALTER TABLE oats_property_interests ADD PRIMARY KEY (property_interest_id);

ALTER TABLE oats_property_interests ADD CONSTRAINT oats_prin_chk CHECK (
	(
		PRIN_TYPE = 'CHG'
		AND PROPERTY_OWNER_TYPE_CODE IS NULL
		AND LAND_TITLE_CHARGE_CODE IS NOT NULL
		AND ACTIVE_IND IS NULL
		AND CHARGE_NBR IS NOT NULL
		AND CHARGE_DESC IS NOT NULL
	)
	OR (
		PRIN_TYPE = 'OWN'
		AND PERSON_ORGANIZATION_ID IS NOT NULL
		AND PROPERTY_OWNER_TYPE_CODE IS NOT NULL
		AND LAND_TITLE_CHARGE_CODE IS NULL
		AND ACTIVE_IND IS NOT NULL
		AND CHARGE_NBR IS NULL
		AND CHARGE_DESC IS NULL
	)
	OR (
		PRIN_TYPE = 'NOOWN'
		AND PERSON_ORGANIZATION_ID IS NULL
		AND PROPERTY_OWNER_TYPE_CODE IS NOT NULL
		AND ACTIVE_IND IS NOT NULL
	)
);

ALTER TABLE oats_property_interests ADD CONSTRAINT oats_prin_pt_chk CHECK (PRIN_TYPE IN ('OWN', 'CHG', 'NOOWN'));

ALTER TABLE oats_property_interests ADD CONSTRAINT oats_prin_ai_chk CHECK (ACTIVE_IND IN ('Y', 'N'));

CREATE TABLE
	oats_alr_appl_decisions (
