COMMENT ON COLUMN oats_alr_appl_decisions.no_statistics_required_ind IS 'NO STATISTICS REQUIRED IND indicates whether the decision should be included in reports.';

COMMENT ON COLUMN oats_alr_appl_decisions.rescinded_date IS 'RESCINDED DATE is the date the decision was rescinded';

COMMENT ON COLUMN oats_alr_appl_decisions.decision_desc IS 'Narrative that describes the ALR APPL DECISION';

COMMENT ON COLUMN oats_alr_appl_decisions.applicant_notification_date IS 'Date the details of the decision was sent to the applicants';

COMMENT ON COLUMN oats_alr_appl_decisions.resolution_number IS 'A system generated sequence number that is suffixed with the year of the decision date and is the public identifier for the decision resolution.  For example 1234-2007.';

COMMENT ON COLUMN oats_alr_appl_decisions.reconsideration_request_id IS 'System generated sequence number to uniquely identify a RECONSIDERATION REQUEST';

COMMENT ON COLUMN oats_alr_appl_decisions.outright_refusal_ind IS 'Indicates whether or not the decision is to outright refuse the ALR APPLICATION or not.  If it is an outright refusal, then no decision ALR APPL COMPONENT can be created.';

COMMENT ON COLUMN oats_alr_appl_decisions.alr_application_id IS 'System generated sequence number to uniquely identify an ALR APPLICATION.  This is also used as the ALR APPLICATION''S external reference number.';

COMMENT ON COLUMN oats_alr_appl_decisions.rescinded_comments IS 'RESCINDED COMMENTS are comments about why the decision was rescinded';

COMMENT ON COLUMN oats_alr_appl_decisions.who_created IS 'WHO CREATED is the IDIR or Oracle userid of the person who created the record.';

COMMENT ON COLUMN oats_alr_appl_decisions.ready_for_decision_date IS 'The date when the application became ready for a decision to be made.';

COMMENT ON COLUMN oats_alr_appl_decisions.revision_count IS 'REVISION COUNT is used by Hibernate to control optimistic locking';

COMMENT ON COLUMN oats_alr_appl_decisions.who_updated IS 'WHO UPDATED is the IDIR or Oracle userid of the person who last updated the record.';

COMMENT ON COLUMN oats_alr_appl_decisions.decision_date IS 'Date the decision was rendered by the Commission.';

COMMENT ON COLUMN oats_alr_appl_decisions.alr_appl_decision_id IS 'System generated sequence number to uniquely identify a ALR APPL DECISION';

ALTER TABLE oats_alr_appl_decisions ADD UNIQUE (reconsideration_request_id);

ALTER TABLE oats_alr_appl_decisions ADD UNIQUE (alr_application_id);

ALTER TABLE oats_alr_appl_decisions ADD PRIMARY KEY (alr_appl_decision_id);

ALTER TABLE oats_alr_appl_decisions ADD CONSTRAINT oats_aadc_chk CHECK (
