CREATE TABLE
	oats_issues (
		issue_id bigint NOT NULL,
		issu_type varchar(10) NOT NULL,
		legacy_issue_nbr varchar(10),
		filed_by_pog_id bigint NOT NULL,
		local_gov_pog_id bigint NOT NULL,
		received_date timestamp NOT NULL,
		description varchar(4000) NOT NULL,
		inquiry_code varchar(10),
		complaint_code varchar(10),
		swo_issued_by varchar(50),
		swo_issued_date timestamp,
		swo_terms varchar(4000),
		swo_review_requested_date timestamp,
		swo_ceo_review_date timestamp,
		swo_ceo_review_description varchar(4000),
		swo_ceo_review_result varchar(4000),
		swo_complied_ind varchar(1),
		recommend_fine_amt double precision,
		recommend_land_remediation_ind varchar(1),
		ceo_hearing_date timestamp,
		ceo_decision_date timestamp,
		fine_amt double precision,
		fine_due_date timestamp,
		fine_received_date timestamp,
		remediation_required_ind varchar(1),
		remediation_due_date timestamp,
		remediation_completion_date timestamp,
		who_created varchar(32) NOT NULL,
		when_created timestamp NOT NULL,
		who_updated varchar(32),
		when_updated timestamp,
		revision_count bigint NOT NULL DEFAULT 0
	);

COMMENT ON TABLE oats_issues IS 'An INQUIRY is a specific type of ISSUE, where someone poses a question to the Commission in regards to land use and the ALR.  The Commission will reference this information if there is an ALR APPLICATION or COMPLAINT within the vicinity.  A COMPLAINT is a specific type of ISSUE that warrants further investigation and may lead to an enforcement action.  If further enforcement action is warranted, the typical course is:  1. Issue a Stop Work Order (SWO).  The property owner may request this order be reviewed with the Chief Executive Officer (CEO).  2. The ALC staff will make recommendations to the CEO as to whether further enforcement action should be applied, in the form of fines and/or land remediation.  3. The CEO may then impose a fine and/or land remediation.  An ISSUE is either an INQUIRY or COMPLAINT regarding land use within the jurisdiction of the ALR.';

COMMENT ON COLUMN oats_issues.swo_terms IS 'The provisions of the stop work order';

COMMENT ON COLUMN oats_issues.recommend_land_remediation_ind IS 'Indicates whether or on the ALC staff recommends remediation to be completed on the land.';

COMMENT ON COLUMN oats_issues.ceo_hearing_date IS 'Date the Chief Executive Officer conducted a hearing to determine if further enforcement action (fines and/or land remediation) should be taken.';

COMMENT ON COLUMN oats_issues.local_gov_pog_id IS 'PERSON ORGANIZATION ID is the system generated unique identifier for PERSON ORGANIZATION.';

COMMENT ON COLUMN oats_issues.description IS 'DESCRIPTION isan attribute further extending the definition of the ISSUE.';

COMMENT ON COLUMN oats_issues.remediation_due_date IS 'Date the remediation must be completed.  Must be supplied if REMEDIATION REQUIRED IND is ''Y'', otherwise must be null.';

COMMENT ON COLUMN oats_issues.filed_by_pog_id IS 'PERSON ORGANIZATION ID is the system generated unique identifier for PERSON ORGANIZATION.';

COMMENT ON COLUMN oats_issues.legacy_issue_nbr IS 'LEGACY ISSUE NBR is the characters used to identify an ISSUE in the legacy ATS application system, where NBR is an abbreviation for number.  For example: 10011-1 or 20200';

COMMENT ON COLUMN oats_issues.complaint_code IS 'User supplied value to uniquely identify an COMPLAINT CODE';

COMMENT ON COLUMN oats_issues.fine_amt IS 'The fine amount the property owner must pay.';

COMMENT ON COLUMN oats_issues.fine_received_date IS 'Date the fine was received by the ALC.  This can only be provided if a FINE AMT is imposed.';

COMMENT ON COLUMN oats_issues.received_date IS 'RECEIVED DATE is a time  the ISSUE was received by the Commission.';

COMMENT ON COLUMN oats_issues.inquiry_code IS 'User supplied value to uniquely identify an ISSUE CODE';

COMMENT ON COLUMN oats_issues.swo_ceo_review_result IS 'Description of the Chief Executive Officer (CEO) results from the review.  This can only be supplied if a SWO REVIEW REQUESTED DATE exists.';

COMMENT ON COLUMN oats_issues.remediation_required_ind IS 'Indicates whether or not the property owner must remediate the property or not';

COMMENT ON COLUMN oats_issues.swo_review_requested_date IS 'Date the property owner requested a stop work order review with the Chief Executive Officer (CEO)';

COMMENT ON COLUMN oats_issues.remediation_completion_date IS 'Date the remediation is considered complete.  Can only be supplied if REMEDIATION REQUIRED IND is ''Y'', otherwise must always be null.';

COMMENT ON COLUMN oats_issues.when_updated IS 'WHEN UPDATED is the date/time that the record was last updated.';

COMMENT ON COLUMN oats_issues.fine_due_date IS 'Date the fine is due by.  This must be provided if a FINE AMT is imposed, or null if not.';

COMMENT ON COLUMN oats_issues.issue_id IS 'System generated sequence number to uniquely identify an ISSUE';

COMMENT ON COLUMN oats_issues.swo_issued_by IS 'Description of who issued the stop work order';

COMMENT ON COLUMN oats_issues.ceo_decision_date IS 'Date the Chief Executive Officer rendered a decision if further enforcement action (fines and/or land remediation) will be taken.';

COMMENT ON COLUMN oats_issues.recommend_fine_amt IS 'The amount of a fine the ALC staff recommended to be imposed on the property owner.';

COMMENT ON COLUMN oats_issues.when_created IS 'WHEN CREATED is the date/time that the record was created.';

COMMENT ON COLUMN oats_issues.revision_count IS 'REVISION COUNT is used by Hibernate to control optimistic locking';

COMMENT ON COLUMN oats_issues.swo_ceo_review_date IS 'Date the Chief Executive Officer (CEO) completed the review of the stop work order.  This can only be supplied if a SWO REVIEW REQUESTED DATE exists.';

COMMENT ON COLUMN oats_issues.who_created IS 'WHO CREATED is the IDIR or Oracle userid of the person who created the record.';

COMMENT ON COLUMN oats_issues.swo_issued_date IS 'Date the stop work order was issued.';

COMMENT ON COLUMN oats_issues.swo_ceo_review_description IS 'Description of the review conducted by the Chief Executive Officer (CEO).  This can only be supplied if a SWO REVIEW REQUESTED DATE exists.';

COMMENT ON COLUMN oats_issues.swo_complied_ind IS 'Indicates whether or not the property owner has complied with the stop work order or not.';

COMMENT ON COLUMN oats_issues.who_updated IS 'WHO UPDATED is the IDIR or Oracle userid of the person who last updated the record.';

