ALTER TABLE oats_issues ADD CONSTRAINT avcon_1238100890_remed_000 CHECK (REMEDIATION_REQUIRED_IND IN ('Y', 'N'));

ALTER TABLE oats_issues ADD CONSTRAINT avcon_1238100890_swo_c_000 CHECK (SWO_COMPLIED_IND IN ('Y', 'N'));

ALTER TABLE oats_issues ADD CONSTRAINT avcon_1238100890_issu__000 CHECK (ISSU_TYPE IN ('INQ', 'CMP'));

ALTER TABLE oats_issues ADD CONSTRAINT oats_issu_chk CHECK (
	(
		issu_type = 'CMP'
		AND inquiry_code IS NULL
		AND complaint_code IS NOT NULL
	)
	OR (
		issu_type = 'INQ'
		AND inquiry_code IS NOT NULL
		AND complaint_code IS NULL
		AND swo_issued_by IS NULL
		AND swo_issued_by IS NULL
		AND swo_issued_date IS NULL
		AND swo_terms IS NULL
		AND swo_review_requested_date IS NULL
		AND swo_ceo_review_date IS NULL
		AND swo_ceo_review_description IS NULL
		AND swo_ceo_review_result IS NULL
		AND swo_complied_ind IS NULL
		AND recommend_fine_amt IS NULL
		AND recommend_land_remediation_ind IS NULL
		AND ceo_hearing_date IS NULL
		AND ceo_decision_date IS NULL
		AND fine_amt IS NULL
		AND fine_due_date IS NULL
		AND fine_received_date IS NULL
		AND remediation_required_ind IS NULL
		AND remediation_due_date IS NULL
		AND remediation_completion_date IS NULL
	)
);

ALTER TABLE oats_issues ADD CONSTRAINT avcon_1238100890_recom_000 CHECK (RECOMMEND_LAND_REMEDIATION_IND IN ('Y', 'N'));

