CREATE TABLE
	oats_properties (
		property_id bigint NOT NULL,
		pid integer,
		pin integer,
		legal_description varchar(4000),
		civic_address varchar(100),
		title_number varchar(15),
		area_size double precision NOT NULL,
		who_created varchar(32) NOT NULL,
		when_created timestamp NOT NULL,
		who_updated varchar(32),
		when_updated timestamp,
		revision_count bigint NOT NULL DEFAULT 0
	);

COMMENT ON TABLE oats_properties IS 'A PROPERTY is a delineated piece of land that can be identified by the Land Titles Office by its P.I.D. (Property Identifier), or is Crown land that can be identified by its P.I.N. (Parcel Identification Number), or other land the can be identified by a legal description.';

COMMENT ON COLUMN oats_properties.when_updated IS 'WHEN UPDATED is the date/time that the record was last updated.';

COMMENT ON COLUMN oats_properties.property_id IS 'System generated sequence number to uniquely identify a PROPERTY';

COMMENT ON COLUMN oats_properties.who_created IS 'WHO CREATED is the IDIR or Oracle userid of the person who created the record.';

COMMENT ON COLUMN oats_properties.title_number IS 'The Land Titles assigned title number associated with a PID';

COMMENT ON COLUMN oats_properties.revision_count IS 'REVISION COUNT is used by Hibernate to control optimistic locking';

COMMENT ON COLUMN oats_properties.when_created IS 'WHEN CREATED is the date/time that the record was created.';

COMMENT ON COLUMN oats_properties.pin IS 'The unique Parcel Identification Number for surveyed Crown parcels.';

COMMENT ON COLUMN oats_properties.who_updated IS 'WHO UPDATED is the IDIR or Oracle userid of the person who last updated the record.';

COMMENT ON COLUMN oats_properties.legal_description IS 'The LEGAL DESCRIPTION uniquely defines a parcel of land typically based on its system of survey, but may include other references or even a metes and bounds narrative. The most common system of survey description consists of a hierarchy of parcel names and values (e.g. lot number, block number), primary land act parcel (e.g. District Lot), land district, registered survey plan, and possibly exceptions.';

COMMENT ON COLUMN oats_properties.pid IS 'Land Title Parcel identifier, displayed in the format 999-999-999';

COMMENT ON COLUMN oats_properties.area_size IS 'The area of the PROPERTY';

COMMENT ON COLUMN oats_properties.civic_address IS 'The free form civic address for the property where and when available';

ALTER TABLE oats_properties ADD PRIMARY KEY (property_id);

CREATE TABLE
	oats_persons (
		person_id bigint NOT NULL,
		per_type varchar(10) NOT NULL,
		first_name varchar(40),
		middle_name varchar(40),
		last_name varchar(40) NOT NULL,
		title varchar(40),
		employee_id bigint,
		who_created varchar(32) NOT NULL,
		when_created timestamp NOT NULL,
		who_updated varchar(32),
		when_updated timestamp,
		revision_count bigint NOT NULL DEFAULT 0
	);

COMMENT ON TABLE oats_persons IS 'A BUSINESS PARTNER is a PERSON which is not a Ministry employee, or a Ministry employee who is acting as a business partner outside of employment function.  A business partner includes contractors, clients and external organizations associated or partnered with the Ministry.  An EMPLOYEE is a  PERSON which is employed by the Ministry and not acting outside of that function.  (An EMPLOYEE of the Ministry could also be considered as a BUSINESS PARTNER if involved with Ministry business activities outside the scope of normal job function.)  A PERSON plays some role in resource inventory management for the BC Government and may belong to an ORGANIZATION.';

COMMENT ON COLUMN oats_persons.who_created IS 'WHO CREATED is the IDIR or Oracle userid of the person who created the record.';

COMMENT ON COLUMN oats_persons.revision_count IS 'REVISION COUNT is used by Hibernate to control optimistic locking';

COMMENT ON COLUMN oats_persons.who_updated IS 'WHO UPDATED is the IDIR or Oracle userid of the person who updated the record.';

COMMENT ON COLUMN oats_persons.when_updated IS 'WHEN UPDATED is the date/time that the record was updated.';

COMMENT ON COLUMN oats_persons.person_id IS 'PERSON ID is the system generated unique identifier for PERSON';

COMMENT ON COLUMN oats_persons.title IS 'The TITLE is the formal appellation attached to the PERSON''s name by virtue of office,  rank, or attainment.';

COMMENT ON COLUMN oats_persons.when_created IS 'WHEN CREATED is the date/time that the record was created.';

COMMENT ON COLUMN oats_persons.middle_name IS 'The MIDDLE NAME is the middle name of a person.';

COMMENT ON COLUMN oats_persons.last_name IS 'The LAST NAME is the last name of a person.';

COMMENT ON COLUMN oats_persons.employee_id IS 'The EMPLOYEE ID is the employee number assigned to the person';

COMMENT ON COLUMN oats_persons.first_name IS 'The FIRST NAME is the first name of a person.';

ALTER TABLE oats_persons ADD PRIMARY KEY (person_id);

ALTER TABLE oats_persons ADD CONSTRAINT avcon_1238100890_per_t_000 CHECK (PER_TYPE IN ('EMP', 'BPA'));

ALTER TABLE oats_persons ADD CONSTRAINT oats_per_chk CHECK (
	(
		per_type = 'BPA'
		AND employee_id IS NULL
	)
	OR (
		per_type = 'EMP'
		AND (
			employee_id IS NULL
			OR employee_id IS NOT NULL
		)
	)
);

